import{_ as a,c as e,o as t,a as i}from"./app.5cb6475b.js";const m=JSON.parse('{"title":"灵魂拷问","description":"","frontmatter":{"title":"灵魂拷问"},"headers":[{"level":2,"title":"1. 有什么意义？(#qa001)","slug":"_1-有什么意义-qa001","link":"#_1-有什么意义-qa001","children":[]}],"relativePath":"qa/index.md"}'),s={name:"qa/index.md"},d=i('<h1 id="灵魂拷问" tabindex="-1">灵魂拷问 <a class="header-anchor" href="#灵魂拷问" aria-hidden="true">#</a></h1><div id="qa001"></div><h2 id="_1-有什么意义-qa001" tabindex="-1">1. 有什么意义？(<a href="#qa001">#qa001</a>) <a class="header-anchor" href="#_1-有什么意义-qa001" aria-hidden="true">#</a></h2><div class="tip custom-block"><p class="custom-block-title">这是一个不认真的问题，但是我们仍然尝试认真的回答：</p><ol><li>“意义”是相对的、高度主观的感受。同一个作品或行为，对不同受众的意义可能是正面的，可能是负面的，也可能完全没有——比如极品钓竿之于钓鱼佬和非爱好者，比如菠萝披萨之于意大利人和中国人，比如飞面神之于信徒和非信徒……</li><li>凹语言对贡献者之外的人有什么意义？不知道——正如刚才提到的：作品对受众的意义，从来不是作者可以定义的——硬要给出一个猜测的话，大概率是：“对大多数开发者来说，没有任何意义”。但那又如何呢，程序员在总人口中只占相当小的比例，这意味任何一门编程语言，哪怕它已经被广泛使用，大多数地球人顶多听它的名字而已，完全没法体会它对自己有什么意义；</li><li>即使在项目组内部，“参与凹语言建设”这件事情的意义，也存在很多不同答案，其中包括：“普通开发者掌握生产工具的尝试”、“大龄码农为逃避内卷的尝试”等等，也很可能就是很简单的一句：“无他，有趣而已”。</li></ol></div>',4),r=[d];function _(c,l,o,n,h,p){return t(),e("div",null,r)}const q=a(s,[["render",_]]);export{m as __pageData,q as default};
